import React from "react";
import SmallStatBlock from "../SmallStatBlock/SmallStatBlock";
import "./PossibleConjures.css";

class PossibleConjures extends React.Component {
    constructor(props) {
        super(props);
        this.conjureMinion = this.conjureMinion.bind(this);
    }

    conjureMinion(name, type, spellSource) {
        fetch(`/api/minions?name=${name}&type=${type}&spellSource=${spellSource}`, {
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            },
            method: "POST",
            body: JSON.stringify({"name": `${name}`, "type": type, "spellSource": spellSource})
        })
        .then(() => {
            this.props.toggleRecentlyConjured();
        })
    }

    render() {
        return (
            <div className="PossibleConjures">
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Bat']} onClick={() => this.conjureMinion('Bat', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Cat']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Crab']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Frog']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Hawk']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Lizard']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Octopus']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Owl']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Poisonous Snake']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Fish']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Rat']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Raven']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Sea Horse']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Spider']}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Weasel']}></SmallStatBlock>
            </div>
        );
    }
}

export default PossibleConjures;
