import React from "react";
import "./SmallStatBlock.css";

class SmallStatBlock extends React.Component {
    render() {
        const conjure = this.props.conjure;
        return (
            <div className="SmallStatBlock">
                <h3 className="monsterTitle">{conjure.name}</h3>
                <div className="SB-size">{conjure.size} fey</div>
                <div className="SB-attributes">
                    <div className="SB-AC"><span className="attributeLabel">AC</span><span className="attributeValue"> 12</span></div>
                    <div className="SB-HP"><span className="attributeLabel">HP</span><span className="attributeValue"> 1</span></div>
                    <div className="SB-Speed"><span className="attributeLabel">Spd</span><span className="attributeValue"> 5ft., fly 30ft</span></div>
                </div>
                <div className="SB-tidbits">
                    <div className="SB-Skills"><span className="tidbitLabel">Skills</span><span className="tidbitValue"> </span></div>
                    <div className="SB-Senses"><span className="tidbitLabel">Senses</span><span className="tidbitValue"> Blindsight 60ft., Pas. Perception 11</span></div>
                    <div className="SB-Languages"><span className="tidbitLabel">Languages</span><span className="tidbitValue"> --</span></div>
                </div>
                <div className="SB-description">
                    <p><em><strong>Echolocation</strong></em> No blindsight while deafened.</p>
                    <p><em><strong>Keen Hearing</strong></em> Adv. on hearing Perception</p>
                </div>
            </div>
        );
    }
}

export default SmallStatBlock;
