import React from "react";
import SmallStatBlock from "../SmallStatBlock/SmallStatBlock";
import "./PossibleConjures.css";

class PossibleConjures extends React.Component {
    constructor(props) {
        super(props);
        this.conjureMinion = this.conjureMinion.bind(this);
    }

    conjureMinion(name, type, spellSource) {
        fetch(`/api/minions?name=${name}&type=${type}&spellSource=${spellSource}`, {
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            },
            method: "POST",
            body: JSON.stringify({"name": `${name}`, "type": type, "spellSource": spellSource})
        })
        .then(() => {
            this.props.toggleRecentlyConjured();
        })
    }

    render() {
        return (
            <div className="PossibleConjures">
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Bat']} onClick={() => this.conjureMinion('Bat', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Cat']} onClick={() => this.conjureMinion('Cat', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Crab']} onClick={() => this.conjureMinion('Crab', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Frog']} onClick={() => this.conjureMinion('Frog', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Hawk']} onClick={() => this.conjureMinion('Hawk', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Lizard']} onClick={() => this.conjureMinion('Lizard', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Octopus']} onClick={() => this.conjureMinion('Octopus', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Owl']} onClick={() => this.conjureMinion('Owl', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Poisonous Snake']} onClick={() => this.conjureMinion('Poisonous Snake', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Fish']} onClick={() => this.conjureMinion('Fish', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Rat']} onClick={() => this.conjureMinion('Rat', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Raven']} onClick={() => this.conjureMinion('Raven', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Sea Horse']} onClick={() => this.conjureMinion('Sea Horse', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Spider']} onClick={() => this.conjureMinion('Spider', 'Familiar', 'Find Familiar')}></SmallStatBlock>
                <SmallStatBlock conjure={this.props.conjures['Find Familiar']['Weasel']} onClick={() => this.conjureMinion('Weasel', 'Familiar', 'Find Familiar')}></SmallStatBlock>
            </div>
        );
    }
}

export default PossibleConjures;
