import React from 'react';
import './App.css';
// import Conjurer from './Components/Conjurer/Conjurer';
import Header from './Components/Header/Header';
import MinionViewer from './Components/MinionViewer/MinionViewer';
import PossibleConjures from './Components/PossibleConjures/PossibleConjures';
import SpellSelector from './Components/SpellSelector/SpellSelector';
import SpellViewer from './Components/SpellViewer/SpellViewer';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {recentlyConjured: false};
    this.toggleRecentlyConjured = this.toggleRecentlyConjured.bind(this);
  }

  toggleRecentlyConjured() {
    this.setState({recentlyConjured: !this.state.recentlyConjured})
  }

  render() {
    return (
      <div className="App">
        <Header></Header>
        <SpellSelector></SpellSelector>
        <SpellViewer></SpellViewer>
        <PossibleConjures toggleRecentlyConjured={this.toggleRecentlyConjured}></PossibleConjures>
        <MinionViewer recentlyConjured={this.state.recentlyConjured}  toggleRecentlyConjured={ this.toggleRecentlyConjured }></MinionViewer>
      </div>
    );
  }
}

export default App;
