import React from "react";
import ConjureButton from "./ConjureButton";
import './Conjurer.css';

class Conjurer extends React.Component {
    constructor(props) {
        super(props);
        this.state = { minions: [], familiars: [] };
        this.getFamiliars = this.getFamiliars.bind(this);
    }

    getFamiliars() {
        fetch(`/api/familiars`)
            .then((res) => res.json())
            .then((data) => {
                console.log(data);
                if(data.rowCount > 0) {
                    this.setState({ familiars: data.rows });
                }
                else {
                    this.setState({ familiars: [] });
                }
            })
    }

    componentDidMount() {
        this.getFamiliars();
    }

    render() {
        return (
            <div className="Conjurer">
                <select name="types" id="types" onChange={this.handleSelect}>
                    <option value="Familiar">Familiar</option>
                </select>
                <ConjureButton onClick={this.handleClick}></ConjureButton>
            </div>
        );
    }
}

export default Conjurer;
