                "Lizard": {
                    name: "Lizard",
                    size: "Tiny",
                    AC: 10,
                    HP: 2,
                    Speed: "20ft., climb 20ft",
                    Skills: "--",
                    Senses: "Darkvision 30ft., Pas. Perception 9",
                    Languages: "--",
                    Description: []
                },
                "Octopus": {
